SELECT \n    a.table_name AS referencing_table,\n    c.column_name AS referencing_column,\n    b.constraint_name AS constraint_name\nFROM \n    all_constraints a\nJOIN \n    all_cons_columns c ON a.constraint_name = c.constraint_name\nJOIN \n    all_constraints b ON c.position = b.position\n                    AND a.r_constraint_name = b.constraint_name\nWHERE \n    b.table_name = 'FOO'\n    AND a.owner = 'YOUR_SCHEMA_NAME' -- Replace with your schema name\nORDER BY \n    a.table_name, c.column_name;