DECLARE @PageNumber INT, @PageSize INT\n\nSET @PageNumber = 1 -- Change to your desired page number\nSET @PageSize = 10   -- Set your desired page size\n\nSELECT TOP (@PageSize) Operator, CustomerIdentifier, DateDisconnected, OtherColumns\nFROM (\n    SELECT Operator, CustomerIdentifier, DateDisconnected, OtherColumns,\n           ROW_NUMBER() OVER (ORDER BY Operator, CustomerIdentifier, DateDisconnected) AS RowNum\n    FROM YourTable\n    ORDER BY Operator, CustomerIdentifier, DateDisconnected\n) AS SubQuery\nWHERE RowNum > (@PageNumber - 1) * @PageSize\nORDER BY RowNum