-- Use consistent naming conventions for tables and columns\nCREATE TABLE employees (\n    employee_id INT PRIMARY KEY,\n    first_name VARCHAR(50),\n    last_name VARCHAR(50),\n    hire_date DATE\n);\n\n-- Use clear comments to explain the purpose of complex queries\nSELECT e.first_name, e.last_name\nFROM employees e\nJOIN departments d ON e.department_id = d.id\nWHERE d.department_name = 'Sales'\n  AND e.hire_date >= '2021-01-01';\n\n-- Capitalize SQL keywords and use lowercase for table/column names\nDELETE FROM customers WHERE last_active < CURRENT_DATE;