-- Step 1: Create a temporary table for mappings\nCREATE TEMPORARY TABLE title_mappings (\n    old_title VARCHAR(255),\n    new_title VARCHAR(255)\n);\n\n-- Step 2: Insert your mapping data into the temporary table\nINSERT INTO title_mappings (old_title, new_title) VALUES\n('a-1', 'a1'),\n('a.1', 'a1'),\n('b-1', 'b1'),\n('b.1', 'b1');\n-- Add more mappings as necessary\n\n-- Step 3: Perform the update with a join to the temporary table\nUPDATE tbl\nSET title = tm.new_title\nFROM title_mappings tm\nWHERE tbl.title = tm.old_title;\n\n-- Step 4: Clean up - drop the temporary table if not needed anymore\nDROP TEMPORARY TABLE IF EXISTS title_mappings;