SELECT key, source_table.hash_val\nFROM (\n    SELECT key,\n           DBMS_CRYPTO.HASH(DBMS_LOB.SUBSTR(clob_value, 32767), 1) AS hash_val -- SHA-1 hash\n    FROM source_table\n) source_table\nMINUS\nSELECT key, target_table.hash_val\nFROM (\n    SELECT key,\n           DBMS_CRYPTO.HASH(DBMS_LOB.SUBSTR(clob_value, 32767), 1) AS hash_val -- SHA-1 hash\n    FROM target_table\n) target_table;