SELECT  Exhibition.venue_id, Exhibition.name, Exhibition.slug,\n           Venue.name, Venue.slug, Venue.location_id,\n           Venue.id, Exhibition.id\n   FROM    exhibitions AS Exhibition FORCE INDEX (idx_active_ends)\n   LEFT JOIN venues AS Venue ON Venue.id = Exhibition.venue_id\n   LEFT JOIN temperatures AS Temperature USING(ref) -- or ON Condition if not matching column names exactly\n   WHERE   Exhibition.active = '1'\n       AND Exhibition.ends <= CURDATE()\n   ORDER BY Temperature.temperature DESC\n   LIMIT 5;