CREATE OR REPLACE FUNCTION audit_change()\nRETURNS TRIGGER AS $$\nBEGIN\n    IF (TG_OP = 'DELETE') THEN\n        INSERT INTO employees_audit (\n            employee_id, first_name, last_name, department, salary,\n            operation\n        ) VALUES (\n            OLD.id, OLD.first_name, OLD.last_name, OLD.department, OLD.salary,\n            'D'\n        );\n        RETURN OLD;\n    elsif (TG_OP = 'UPDATE') THEN\n        INSERT INTO employees_audit (\n            employee_id, first_name, last_name, department, salary,\n            operation\n        ) VALUES (\n            OLD.id, OLD.first_name, OLD.last_name, OLD.department, OLD.salary,\n            'U'\n        );\n        RETURN NEW;\n    elsif (TG_OP = 'INSERT') THEN\n        INSERT INTO employees_audit (\n            employee_id, first_name, last_name, department, salary,\n            operation\n        ) VALUES (\n            NEW.id, NEW.first_name, NEW.last_name, NEW.department, NEW.salary,\n            'I'\n        );\n        RETURN NEW;\n    END IF;\n    RETURN NULL; -- result is ignored since this is an AFTER trigger\nEND;\n$$ LANGUAGE plpgsql;