-- For Publisher 1\nUSE YourDatabase;\nEXEC sp_changearticle \n    @publication = N'YourPublicationName', \n    @article = N'TableWithIdentityColumn',\n    @property = N'identityrangemanagementoption',\n    @value = N'manual';\n\nEXEC sp_addextendedproperty N'MSrepl_identity_range_start', 1, N'user_table', N'TableWithIdentityColumn';\nEXEC sp_addextendedproperty N'MSrepl_identity_range_end', 1000000, N'user_table', N'TableWithIdentityColumn';\n\n-- For Publisher 2\nUSE YourDatabase;\nEXEC sp_changearticle \n    @publication = N'YourPublicationName', \n    @article = N'TableWithIdentityColumn',\n    @property = N'identityrangemanagementoption',\n    @value = N'manual';\n\nEXEC sp_addextendedproperty N'MSrepl_identity_range_start', 1000001, N'user_table', N'TableWithIdentityColumn';\nEXEC sp_addextendedproperty N'MSrepl_identity_range_end', 2000000, N'user_table', N'TableWithIdentityColumn';