WITH ID1Values AS (\n    SELECT Values AS Value FROM your_table WHERE ID = 1\n),\nID2Values AS (\n    SELECT Values AS Value FROM your_table WHERE ID = 2\n)\nSELECT v1.Value + '(-)' AS Difference\nFROM ID1Values v1\nLEFT JOIN ID2Values v2 ON v1.Value = v2.Value\nWHERE v2.Value IS NULL\n\nUNION ALL\n\nSELECT v2.Value + '(+)'\nFROM ID1Values v1\nRIGHT JOIN ID2Values v2 ON v1.Value = v2.Value\nWHERE v1.Value IS NULL;