CREATE PROCEDURE CalculatePrice\n    @BenchmarkA DECIMAL(18, 4),\n    @BenchmarkB DECIMAL(18, 4),\n    @FormulaText NVARCHAR(MAX),\n    @CalculatedPrice DECIMAL(18, 4) OUTPUT\nAS\nBEGIN\n    DECLARE @SQL NVARCHAR(MAX);\n    SET @SQL = N'SET @CalculatedPrice = ' + @FormulaText;\n    EXEC sp_executesql @SQL, N'@BenchmarkA DECIMAL(18, 4), @BenchmarkB DECIMAL(18, 4), @CalculatedPrice DECIMAL(18, 4) OUTPUT', @BenchmarkA, @BenchmarkB, @CalculatedPrice OUTPUT;\nEND