-- Creating indexes\n   CREATE INDEX idx_employee_name ON employees(name);\n   \n   -- Dropping unnecessary indexes to save space and improve write performance\n   DROP INDEX unused_index_name ON table_name;\n   \n   -- Partitioning tables for better query response time and easier maintenance\n   ALTER TABLE sales PARTITION BY RANGE (sale_date) (\n       PARTITION p0 VALUES LESS THAN ('2016-01-01'),\n       PARTITION p1 VALUES LESS THAN ('2017-01-01')\n   );\n   \n   -- Denormalization can be used to reduce the need for JOIN operations, but it should be done carefully\n   ALTER TABLE employees ADD COLUMN department_name VARCHAR(50);