DECLARE @ImageFile NVARCHAR(100)\nDECLARE @OLEResult INT, @FileStream INT, @BinaryData VARBINARY(MAX)\nDECLARE @FileLength BIGINT\n\n-- Set the path to your image file\nSET @ImageFile = 'C:\path_to_your_image\your_image.jpg'\n\n-- Create an instance of FileSystemObject and get the length of the file\nEXECUTE @OLEResult = sp_OACreate 'Scripting.FileSystemObject', @FSO OUT\nIF @OLEResult <> 0 PRINT 'Error: Creating OLE FileSystemObject'\n\n-- Open the file\nEXECUTE @OLEResult = sp_OAMethod @FSO, 'GetFile', @FileRef OUT, (SELECT @ImageFile)\nIF @OLEResult <> 0 SELECT @OLEResult AS ErrorCode\n\n-- Get the size of the file in bytes and store it to @FileLength variable\nEXECUTE @OLEResult = sp_OAGetProperty @FileRef, 'Size', @FileLength OUT\nIF @OLEResult <> 0 SELECT @FileLength AS FileLength\n\n-- Create an instance of BinaryStream object for reading binary files\nEXECUTE @OLEResult = sp_OACreate 'ADODB.Stream', @FileStream OUT\nIF @OLEResult <> 0 PRINT 'Error: Creating OLE Stream'\n\n-- Set the source file stream type to binary\nEXECUTE @OLEResult = sp_OAMethod @FileStream, 'Type=', NULL, 1 -- adTypeBinary=1\nIF @OLEResult <> 0 PRINT 'Error: Setting Source FileStream Type to Binary'\n\n-- Open the stream\nEXECUTE @OLEResult = sp_OAMethod @FileStream, 'Open'\nIF @OLEResult <> 0 PRINT 'Error: Opening source FileStream'\n\n-- Load data from file into binary variable @BinaryData\nEXECUTE @OLEResult = sp_OAMethod @FileStream, 'LoadFromFile', NULL, (SELECT @ImageFile)\nIF @OLEResult <> 0 PRINT 'Error: Loading file to Source FileStream'\nEXECUTE @OLEResult = sp_OAGetProperty @FileStream, 'Read', @BinaryData OUT\nIF @OLEResult <> 0 PRINT 'Error: Reading source FileStream'\n\n\n-- Insert data into your table\nINSERT INTO YourTable(ImageColumnName) VALUES (@BinaryData)\n\n-- Clean up: Close stream and destroy objects\nEXECUTE @OLEResult = sp_OAMethod @FileStream, 'Close'\nEXECUTE SP_OADestroy @FileStream\nEXECUTE SP_OADestroy @FileRef\nEXECUTE SP_OADestroy @FSO\n\n-- Check errors\nIF @OLEResult <> 0 SELECT @OLEResult AS ErrorCode, ERROR_TEXT(@OLEResult) AS ErrorMessage