-- Create Sequences\nCREATE SEQUENCE SEQ_A START WITH 1 INCREMENT BY 1;\nCREATE SEQUENCE SEQ_B START WITH 1 INCREMENT BY 1;\nCREATE SEQUENCE SEQ_C START WITH 1 INCREMENT BY 1;\nCREATE SEQUENCE SEQ_D START WITH 1 INCREMENT BY 1;\n\n-- Create Tables with Primary Keys and Foreign Key Constraints\nCREATE TABLE A (\n    Aid NUMBER PRIMARY KEY,\n    -- other columns...\n);\n\nCREATE TABLE B (\n    Bid NUMBER PRIMARY KEY,\n    Aid NUMBER,\n    bnum NUMBER,\n    FOREIGN KEY (Aid) REFERENCES A(Aid)\n);\n\nCREATE TABLE C (\n    Cid NUMBER PRIMARY KEY,\n    Aid NUMBER,\n    cnum NUMBER,\n    FOREIGN KEY (Aid) REFERENCES A(Aid)\n);\n\nCREATE TABLE D (\n    Did NUMBER PRIMARY KEY,\n    Bid NUMBER,\n    Cid NUMBER,\n    FOREIGN KEY (Bid) REFERENCES B(Bid),\n    FOREIGN KEY (Cid) REFERENCES C(Cid)\n);\n\n-- Create Triggers to Populate ID Fields Using Sequences\n\nCREATE OR REPLACE TRIGGER TRG_Generate_AID \nBEFORE INSERT ON A\nFOR EACH ROW\nBEGIN\n  SELECT SEQ_A.NEXTVAL INTO :NEW.Aid FROM dual;\nEND;\n\nCREATE OR REPLACE TRIGGER TRG_Generate_BID \nBEFORE INSERT ON B\nFOR EACH ROW\nBEGIN\n  SELECT SEQ_B.NEXTVAL INTO :NEW.Bid FROM dual;\nEND;\n\nCREATE OR REPLACE TRIGGER TRG_Generate_CID \nBEFORE INSERT ON C\nFOR EACH ROW\nBEGIN\n  SELECT SEQ_C.NEXTVAL INTO :NEW.Cid FROM dual;\nEND;\n\nCREATE OR REPLACE TRIGGER TRG_Generate_DID \nBEFORE INSERT ON D\nFOR EACH ROW\nBEGIN\n  SELECT SEQ_D.NEXTVAL INTO :NEW.Did FROM dual;\nEND;