-- Instead of creating separate stored procedures for similar tasks,\n-- create one procedure with parameters\n\nCREATE PROCEDURE GetEmployeeDetails\n    @DepartmentID INT = NULL,\n    @Role VARCHAR(50) = NULL\nAS\nBEGIN\n    SELECT \n        EmployeeID, \n        Name, \n        Position, \n        Department\n    FROM Employees\n    WHERE (@DepartmentID IS NULL OR DepartmentID = @DepartmentID)\n      AND (@Role IS NULL OR Position = @Role);\nEND