-- Step 1: Estimate Row Count\nSELECT COUNT(*) INTO @row_count FROM your_table;\n\n-- Step 2: Generate Random IDs and Select Rows\nSET @sample_size = 10000;\nSET @random_ids = (SELECT GROUP_CONCAT(\n    FLOOR(1 + RAND() * (@row_count - 1)) \n    SEPARATOR ',') \nFROM INFORMATION_SCHEMA.TABLES LIMIT @sample_size);\n\n-- Step 3: Fetch Rows Using Generated IDs\nPREPARE stmt FROM 'SELECT * FROM your_table WHERE id IN (?)';\nEXECUTE stmt USING @random_ids;\nDEALLOCATE PREPARE stmt;