WITH RECURSIVE RecursiveTree AS (\n    -- Anchor member: select the initial parent node\n    SELECT id, name, parentid, value\n    FROM example\n    WHERE parentid = ? -- Replace with actual parentid or parameter\n\n    UNION ALL\n\n    -- Recursive member: join to get all children of the current node\n    SELECT e.id, e.name, e.parentid, e.value\n    FROM example e\n    JOIN RecursiveTree rt ON e.parentid = rt.id\n)\n-- Finally, sum up the values from the recursive result set\nSELECT SUM(value) AS total_value\nFROM RecursiveTree;