WITH RECURSIVE LocationPath AS (\n    SELECT\n        ID,\n        LocationName,\n        ParentID\n    FROM\n        Locales\n    WHERE\n        ID = ? -- Replace with your specific locale ID\n    UNION ALL\n    SELECT\n        l.ID,\n        l.LocationName,\n        l.ParentID\n    FROM\n        Locales l\n    INNER JOIN LocationPath p ON l.ID = p.ParentID\n)\nSELECT * \nFROM LocationPath\nORDER BY ID; -- You may want to order by a hierarchy level if tracked