SELECT \n    t1_parent.id AS parent_id, \n    t1_child.id AS child_id, \n    t1_parent.description AS parent_description,\n    t1_child.description AS child_description,\n    SYS_CONNECT_BY_PATH(t1_child.description, '/') AS path\nFROM \n    t2\nJOIN \n    t1 t1_parent ON t2.parent = t1_parent.id\nJOIN \n    t1 t1_child ON t2.child = t1_child.id\nSTART WITH t2.parent IS NULL -- or some condition that identifies root nodes\nCONNECT BY PRIOR t2.child = t2.parent;