-- Disable constraints (if any)\nALTER TABLE dbo.MyTable NOCHECK CONSTRAINT ALL;\n\n-- Drop the index if exists\nIF EXISTS(SELECT * FROM sys.indexes WHERE name = 'IX_MyColumn' AND object_id = OBJECT_ID('MyTable'))\n    DROP INDEX IX_MyColumn ON dbo.MyTable;\n\n-- Alter column to NOT NULL\nALTER TABLE dbo.MyTable ALTER COLUMN myColumn int NOT NULL;\n\n-- Recreate the index if needed\nCREATE INDEX IX_MyColumn ON dbo.MyTable (myColumn);\n\n-- Re-enable constraints\nALTER TABLE dbo.MyTable CHECK CONSTRAINT ALL;