CREATE FUNCTION dbo.fnCRC32(@Input VARCHAR(MAX))\nRETURNS INT\nAS\nBEGIN\n    DECLARE @i INT = 1,\n            @CRC INT = -1,\n            @Byte INT,\n            @C INT;\n\n    WHILE (@i <= LEN(@Input))\n    BEGIN\n        SELECT @Byte = ASCII(SUBSTRING(@Input, @i, 1)),\n               @C = @CRC ^ @Byte;\n\n        SET @C = ((@C & 1) * 0xEDB88320) ^\n                (((@C >> 1) & 1) * 0xDB70F336) ^\n                (((@C >> 2) & 1) * 0xAB45AF6B) ^\n                (((@C >> 3) & 1) * 0x9382A470) ^\n                (((@C >> 4) & 1) * 0x36AED0E1) ^\n                (((@C >> 5) & 1) * 0xFA897DDF) ^\n                (((@C >> 6) & 1) * 0x7FFEC6E4) ^\n                (@C >> 7);\n        SET @CRC = ((@CRC >> 8) & 0xFFFFFF) ^ @C;\n        SET @i = @i + 1;\n    END\n\n    RETURN ~(@CRC)\nEND\nGO\n\n-- Example usage:\nSELECT dbo.fnCRC32('your_string_here') AS CRC32Value;