DECLARE @StartDate DATE = '2023-10-01';\nDECLARE @EndDate DATE = '2023-10-31';\n\nWITH DateRange AS (\n    SELECT @StartDate AS WorkDate\n    UNION ALL\n    SELECT DATEADD(DAY, 1, WorkDate)\n    FROM DateRange\n    WHERE DATEADD(DAY, 1, WorkDate) <= @EndDate\n)\n\nSELECT COUNT(*) AS NumberOfWorkDays\nFROM DateRange\nWHERE DATEPART(WEEKDAY, WorkDate) NOT IN (7, 1); -- Assuming Sunday=1 and Saturday=7 in DATEPART