-- Define a record type that represents the structure of a row\nCREATE OR REPLACE TYPE employee_record AS OBJECT (\n  id NUMBER,\n  name VARCHAR2(100)\n);\n/\n\n-- Create a nested table type as a collection of these records\nCREATE OR REPLACE TYPE employee_table AS TABLE OF employee_record;\n/\n\nDECLARE\n  -- Declare a variable to hold the nested table\n  emp_records employee_table := employee_table();\nBEGIN\n  -- Extend and add elements to the nested table\n  emp_records.EXTEND;\n  emp_records(emp_records.LAST) := employee_record(1, 'John Doe');\n  \n  emp_records.EXTEND;\n  emp_records(emp_records.LAST) := employee_record(2, 'Jane Smith');\n\n  -- Iterate over the nested table records\n  FOR i IN 1..emp_records.COUNT LOOP\n    DBMS_OUTPUT.PUT_LINE('ID: ' || emp_records(i).id || ', Name: ' || emp_records(i).name);\n  END LOOP;\nEND;\n/