DECLARE\n    l_cur    INTEGER := DBMS_SQL.OPEN_CURSOR;\n    l_query  VARCHAR2(32767) := 'SELECT * FROM your_table'; -- Replace with the dynamic query\n    l_col_cnt  INTEGER;\n    l_desc_rec   DBMS_SQL.DESC_TAB;\n    l_retval   NUMBER;\n\nBEGIN\n    -- Parse the SQL statement\n    DBMS_SQL.PARSE(l_cur, l_query, DBMS_SQL.NATIVE);\n\n    -- Describe columns of the cursor\n    DBMS_SQL.DESCRIBE_COLUMNS2(l_cur, l_col_cnt, l_desc_rec);\n\n    -- Execute the query\n    l_retval := DBMS_SQL.EXECUTE(l_cur);\n\n    -- Column value variables (adjust according to types you expect)\n    DECLARE\n        l_varchar2_val VARCHAR2(4000);\n        l_number_val NUMBER;\n        l_date_val DATE;\n        l_col_value  RAW(512);  -- Raw type large enough for conversion\n    BEGIN\n        FOR i IN 1..l_col_cnt LOOP\n            CASE l_desc_rec(i).col_type\n                WHEN 1 THEN -- VARCHAR2\n                    DBMS_SQL.DEFINE_COLUMN(l_cur, i, l_varchar2_val, 4000);\n                WHEN 2 THEN -- NUMBER\n                    DBMS_SQL.DEFINE_COLUMN(l_cur, i, l_number_val);\n                WHEN 12 THEN -- DATE\n                    DBMS_SQL.DEFINE_COLUMN(l_cur, i, l_date_val);\n                ELSE \n                    DBMS_SQL.DEFINE_COLUMN_RAW(l_cur, i, l_col_value, 512);\n            END CASE;\n        END LOOP;\n\n        WHILE DBMS_SQL.FETCH_ROWS(l_cur) > 0 LOOP\n            FOR i IN 1..l_col_cnt LOOP\n                CASE l_desc_rec(i).col_type\n                    WHEN 1 THEN -- VARCHAR2\n                        DBMS_SQL.COLUMN_VALUE_CHAR(l_cur, i, l_varchar2_val);\n                        DBMS_OUTPUT.PUT_LINE('Column ' || TO_CHAR(i) || ': ' || l_varchar2_val); -- Example output\n                    WHEN 2 THEN -- NUMBER\n                        DBMS_SQL.COLUMN_VALUE_NUMBER(l_cur, i, l_number_val);\n                        DBMS_OUTPUT.PUT_LINE('Column ' || TO_CHAR(i) || ': ' || TO_CHAR(l_number_val));\n                    WHEN 12 THEN -- DATE\n                        DBMS_SQL.COLUMN_VALUE_DATE(l_cur, i, l_date_val);\n                        DBMS_OUTPUT.PUT_LINE('Column ' || TO_CHAR(i) || ': ' || TO_CHAR(l_date_val, 'YYYY-MM-DD'));\n                    ELSE \n                        DBMS_SQL.COLUMN_VALUE_RAW(l_cur, i, l_col_value);\n                        -- Handle other types\n                END CASE;\n            END LOOP;\n        END LOOP;\n\n        -- Close cursor\n        DBMS_SQL.CLOSE_CURSOR(l_cur);\n\n    EXCEPTION\n        WHEN OTHERS THEN\n          IF DBMS_SQL.IS_OPEN(l_cur) THEN\n            DBMS_SQL.CLOSE_CURSOR(l_cur);\n          END IF;\n          RAISE;\n    END;\nEND;