DECLARE\n    v_blob              BLOB;\n    v_bfile             BFILE;\n    v_dest_offset       NUMBER := 1;\n    v_src_offset        NUMBER := 1;\n    v_lang_context      INTEGER := DBMS_LOB.DEFAULT_LANG_CTX;\n    v_warning           INTEGER;\nBEGIN\n    -- Create an empty BLOB variable\n    DBMS_LOB.CREATETEMPORARY(v_blob, TRUE);\n\n    -- Specify the directory object and file name\n    v_bfile := BFILENAME('MY_DIRECTORY_OBJECT', 'my_file.txt');\n\n    -- Open the binary file\n    DBMS_LOB.OPEN(v_bfile, DBMS_LOB.LOB_READONLY);\n\n    -- Load from the binary file into the temporary LOB\n    DBMS_LOB.LOADFROMFILE(v_blob, v_bfile, DBMS_LOB.GETLENGTH(v_bfile));\n\n    -- Now you can insert the BLOB into your table or use it as needed\n    INSERT INTO my_table (blob_column) VALUES (v_blob);\n\n    -- Close the binary file\n    DBMS_LOB.CLOSE(v_bfile);\n\n    -- Free the temporary LOB if necessary\n    IF DBMS_LOB.ISTEMPORARY(v_blob) = 1 THEN\n        DBMS_LOB.FREETEMPORARY(v_blob);\n    END IF;\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Handle exceptions and close file if it is still open\n        IF DBMS_LOB.ISOPEN(v_bfile) > 0 THEN\n            DBMS_LOB.CLOSE(v_bfile);\n        END IF;\n\n        -- Free the temporary LOB\n        IF DBMS_LOB.ISTEMPORARY(v_blob) = 1 THEN\n            DBMS_LOB.FREETEMPORARY(v_blob);\n        END IF;\n\n        RAISE;\nEND;\n/