DECLARE\n    p_job_id JOB.JOB_ID%TYPE := 123; -- Replace with your actual job ID\n    out_cursor MYGEN.sqlcur;\nBEGIN\n    APP_DB1.GET_JOB(p_JOB_ID => p_job_id, outCursor => out_cursor);\n\n    -- Fetch and display the results from the OUT CURSOR\n    FOR rec IN out_cursor LOOP\n        DBMS_OUTPUT.PUT_LINE('Job found: ' || rec.JOB_ID);\n        -- Add additional columns as required, e.g., rec.OTHER_COLUMN\n    END LOOP;\n    \n    -- Close the cursor to avoid leaks\n    CLOSE out_cursor;\nEND;\n/