-- Example procedure that logs entry and exit points\nCREATE OR REPLACE PROCEDURE my_procedure(p_param1 IN NUMBER) AS\nBEGIN\n    -- Log the procedure start point\n    logger.log('DEBUG', 'Starting my_procedure with param=' || p_param1);\n\n    -- Procedure logic goes here\n\n    -- Log the procedure end point\n    logger.log('DEBUG', 'Ending my_procedure');\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Log error\n        logger.log_error('An error occurred in my_procedure');\nEND;