DECLARE\n    v_sql VARCHAR2(32767);\n    CURSOR col_cursor(p_schema IN VARCHAR2, p_table IN VARCHAR2) IS\n        SELECT COLUMN_NAME FROM ALL_TAB_COLUMNS\n        WHERE TABLE_SCHEMA = UPPER(p_schema)\n          AND TABLE_NAME = UPPER(p_table)\n        ORDER BY COLUMN_ID;\nBEGIN\n    v_sql := 'INSERT INTO Schema1.tableA (';\n    FOR col_rec IN col_cursor('Schema1', 'tableA') LOOP\n        v_sql := v_sql || col_rec.COLUMN_NAME || ', ';\n    END LOOP;\n\n    -- Remove the trailing comma and space, then add the SELECT part of v_sql\n    v_sql := RTRIM(v_sql, ', ') || ') SELECT ';\n    \n    FOR col2_rec IN col_cursor('Schema2', 'tableA') LOOP\n        v_sql := v_sql || col2_rec.COLUMN_NAME || ', ';\n    END LOOP;\n\n    -- Remove the trailing comma and space after column names in SELECT clause\n    v_sql := RTRIM(v_sql, ', ') || ' FROM Schema2.tableA';\n    \n    EXECUTE IMMEDIATE v_sql;\nEND;\n/