DECLARE\n    xml_content CLOB;\nBEGIN\n    -- Load the XML content from a URL or local file (if possible)\n    SELECT UTL_HTTP.READ_CLOB(UTL_HTTP.GET_RESPONSE(UTL_HTTP.OPEN_URL('http://example.com/badges.xml')).CLOB) INTO xml_content FROM dual;\n\n    -- Insert parsed data into badges table\n    INSERT INTO badges (user_id, name, badge_date)\n    SELECT x.*\n    FROM XMLTABLE('/badges/row'\n        PASSING xmltype(xml_content)\n        COLUMNS (\n            user_id NUMBER PATH '@UserId',\n            name VARCHAR2(40) PATH '@Name',\n            badge_date DATE PATH '@Date' DEFAULT NULL ON EMPTY\n        )\n    ) x;\nEND;\n/