DECLARE\n    v_current_schema VARCHAR2(30);\nBEGIN\n    -- Save the current schema\n    SELECT sys_context('USERENV', 'CURRENT_SCHEMA') INTO v_current_schema FROM dual;\n\n    -- Change the current schema (example)\n    EXECUTE IMMEDIATE 'ALTER SESSION SET CURRENT_SCHEMA=OTHER_SCHEMA';\n\n    -- Perform your operations here...\n\n    -- Revert to the original schema\n    EXECUTE IMMEDIATE 'ALTER SESSION SET CURRENT_SCHEMA=' || v_current_schema;\nEND;