DECLARE\n  event_message VARCHAR2(100);\nBEGIN\n  -- Prepare a simple message to be posted\n  event_message := 'Significant transaction spike detected!';\n\n  -- Post the message using DBMS_AQ.POST\n  DBMS_AQ.POST(\n    queue_name => 'ANALYTICS_EVENTS_QUEUE',\n    enqueue_options => NULL,\n    message_properties => NULL,\n    payload => SYS.ANYDATA.CONVERT_VARCHAR2(event_message),\n    msgid => NULL\n  );\n\n  COMMIT; -- Ensure the message is posted to the queue\nEND;\n/