-- Define the types for arrays\nCREATE OR REPLACE PACKAGE user_pkg AS\n    TYPE name_t IS TABLE OF VARCHAR2(50) INDEX BY PLS_INTEGER;\n    TYPE surname_t IS TABLE OF VARCHAR2(50) INDEX BY PLS_INTEGER;\n    TYPE dt_birth_t IS TABLE OF DATE INDEX BY PLS_INTEGER;\n\n    -- Procedure to insert multiple users\n    PROCEDURE insert_users(\n        p_names IN name_t,\n        p_surnames IN surname_t,\n        p_dt_births IN dt_birth_t\n    );\nEND user_pkg;\n/\n\n-- Implement the procedure in the package body\nCREATE OR REPLACE PACKAGE BODY user_pkg AS\n\n    PROCEDURE insert_users(\n        p_names IN name_t,\n        p_surnames IN surname_t,\n        p_dt_births IN dt_birth_t) IS\n    BEGIN\n        -- Ensure all arrays are of the same length\n        IF p_names.COUNT != p_surnames.COUNT \n            OR p_surnames.COUNT != p_dt_births.COUNT THEN\n            RAISE_APPLICATION_ERROR(-20001, 'All input arrays must have the same number of elements');\n        END IF;\n\n        -- Insert users in bulk\n        FORALL i IN 1..p_names.COUNT\n        INSERT INTO "USER" (Name, Surname, Dt_Birth)\n        VALUES(p_names(i), p_surnames(i), p_dt_births(i));\n\n    END insert_users;\n\nEND user_pkg;\n/