CREATE OR REPLACE PROCEDURE test_date_input (p_mydate IN VARCHAR2) IS\n    v_date DATE;\nBEGIN\n    -- Try to convert the input into a date\n    BEGIN\n        v_date := TO_DATE(p_mydate, 'DD-MON-YYYY');\n    EXCEPTION\n        WHEN OTHERS THEN\n            -- Handle the case where conversion fails\n            IF sqlcode = -1843 THEN  -- ORA-01843: not a valid month\n                DBMS_OUTPUT.PUT_LINE('Invalid date format.');\n            ELSIF sqlcode = -1858 THEN  -- ORA-01858: a non-numeric character was found where a numeric was expected\n                DBMS_OUTPUT.PUT_LINE('Non-numeric characters in the date.');\n            ELSE\n                DBMS_OUTPUT.PUT_LINE('Date conversion error: ' || SQLERRM);\n            END IF;\n            RETURN;  -- Exit procedure if date is invalid\n    END;\n\n    -- If no exception, proceed with valid date\n    DBMS_OUTPUT.PUT_LINE('Valid date: ' || v_date);\nEND test_date_input;\n/