DECLARE\n  CURSOR c_informix_data IS\n    SELECT * FROM informix_table;\n  TYPE t_informix_data_table IS TABLE OF c_informix_data%ROWTYPE INDEX BY PLS_INTEGER;\n  v_data t_informix_data_table;\n  v_batch_size NUMBER := 100; -- Adjust batch size as needed\nBEGIN\n  OPEN c_informix_data;\n  LOOP\n    FETCH c_informix_data BULK COLLECT INTO v_data LIMIT v_batch_size;\n    EXIT WHEN v_data.COUNT = 0;\n\n    FORALL i IN 1..v_data.COUNT\n      INSERT INTO oracle_table (col1, col2) VALUES (v_data(i).col1, v_data(i).col2);\n\n    COMMIT; -- Commit after batch inserts\n\n    FORALL i IN 1..v_data.COUNT\n      UPDATE informix_table\n      SET new_data_flag = 'Y'\n      WHERE unique_key_column = v_data(i).unique_key_column;\n\n    COMMIT; -- Commit after batch updates\n  END LOOP;\n  CLOSE c_informix_data;\nEND;