DECLARE\n    v_alias VARCHAR2(100) := 'This "is" an alias'; -- Example alias value retrieved from user data\n    v_sql   VARCHAR2(500);\nBEGIN\n    -- Replace single double quotes with two double quotes for SQL compatibility\n    v_alias := REPLACE(v_alias, '"', '""');\n    \n    -- Construct the dynamic SQL statement using the modified alias\n    v_sql := 'SELECT table1.Col1 AS "' || v_alias || '" FROM table1';\n    \n    -- Execute the dynamic SQL statement if needed\n    EXECUTE IMMEDIATE v_sql;\nEND;