DECLARE\n  l_timestamp NUMBER;\n  l_current_time NUMBER;\n  l_elapsed NUMBER;\nBEGIN\n  -- Initial timestamp capture\n  l_timestamp := dbms_utility.get_time;\n\n  -- Simulate some processing delay\n  DBMS_LOCK.SLEEP(1);\n\n  -- Current time capture\n  l_current_time := dbms_utility.get_time;\n\n  -- Check for rollover\n  IF l_current_time < l_timestamp THEN\n    l_elapsed := (2147483647 - l_timestamp) + (l_current_time + 2147483648);\n  ELSE\n    l_elapsed := l_current_time - l_timestamp;\n  END IF;\n\n  -- Output elapsed time in hundredths of a second\n  DBMS_OUTPUT.PUT_LINE('Elapsed time: ' || l_elapsed);\nEND;