DECLARE\n  v_p1_value VARCHAR2(50);\n  v_p2_value VARCHAR2(50);\nBEGIN\n  -- Retrieve the value of p1 from the URL\n  v_p1_value := :PARAMETER.'P1';\n  \n  -- Retrieve the value of p2 from the URL\n  v_p2_value := :PARAMETER.'P2';\n  \n  -- Use DBMS_OUTPUT.PUT_LINE to display values (for debugging purposes)\n  DBMS_OUTPUT.PUT_LINE('Value of P1: ' || v_p1_value);\n  DBMS_OUTPUT.PUT_LINE('Value of P2: ' || v_p2_value);\nEND;