DECLARE\n    TYPE t_my_table IS TABLE OF my_table%ROWTYPE;\n    v_data t_my_table;\nBEGIN\n    -- Select all rows that need processing into the array\n    SELECT * BULK COLLECT INTO v_data FROM my_table WHERE my_column IS NOT NULL;\n\n    FORALL i IN 1..v_data.COUNT SAVE EXCEPTIONS\n        UPDATE my_table SET my_column = do_something(my_column)\n        WHERE ROWID = v_data(i).ROWID;\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Log the error details\n        BEGIN\n            FOR j IN 1 .. SQL%BULK_EXCEPTIONS.COUNT LOOP\n                INSERT INTO log_error (msg)\n                VALUES ('Error updating value with ROWID: ' || v_data(SQL%BULK_EXCEPTIONS(j).ERROR_INDEX).ROWID ||\n                        ', Error Message: ' || SQLERRM(-SQL%BULK_EXCEPTIONS(j).ERROR_CODE));\n            END LOOP;\n        EXCEPTION\n            WHEN OTHERS THEN\n                INSERT INTO log_error (msg)\n                VALUES ('Failed to log error details for bulk update operation.');\n        END;\nEND;\n/