DECLARE\n  i NUMBER := 0;\nBEGIN\n  <<outer_loop>>\n  LOOP\n    i := i + 1;\n\n    -- Exit when your condition is met to stop the outer loop\n    EXIT WHEN i > 5;\n\n    IF MOD(i, 2) = 0 THEN\n      NULL; -- Your first IF logic here\n\n      IF i = 4 THEN\n        -- Instead of CONTINUE, use GOTO to skip the rest of this iteration\n        GOTO continue_label;\n      END IF;\n    ELSE\n      DBMS_OUTPUT.PUT_LINE('Odd number: ' || i);\n    END IF;\n\n    <<continue_label>>\n    NULL; -- Place a placeholder for continuing the loop\n\n  END LOOP outer_loop;\nEND;