CREATE OR REPLACE PROCEDURE fetch_data(p_schema IN VARCHAR2) IS\n  v_sql   VARCHAR2(1000);\n  my_cursor SYS_REFCURSOR;\n  my_field  <your_column_type>;  -- Replace <your_column_type> with the actual data type of my_field\nBEGIN\n  -- Construct the dynamic SQL statement\n  v_sql := 'SELECT my_field FROM ' || p_schema || '.my_table';\n  \n  -- Open the cursor for the dynamic SQL\n  OPEN my_cursor FOR v_sql;\n  \n  -- Fetch and process each row from the cursor\n  LOOP\n    FETCH my_cursor INTO my_field;\n    EXIT WHEN my_cursor%NOTFOUND;\n    \n    -- Process your field here, e.g., DBMS_OUTPUT.PUT_LINE(my_field);\n  END LOOP;\n  \n  CLOSE my_cursor;\nEND fetch_data;