CREATE OR REPLACE FUNCTION get_current_schema RETURN VARCHAR2 IS\n    v_schema_name VARCHAR2(30);\nBEGIN\n    SELECT owner\n      INTO v_schema_name\n      FROM user_objects\n     WHERE object_name = 'YOUR_FUNCTION_NAME' -- Replace with the function's name in uppercase\n       AND OBJECT_TYPE = 'FUNCTION';\n\n    RETURN v_schema_name;\nEXCEPTION\n    WHEN NO_DATA_FOUND THEN\n        RETURN NULL; -- or handle as appropriate\nEND get_current_schema;