DECLARE\n    CURSOR emp_cursor IS\n        SELECT employee_id, salary FROM employees FOR UPDATE;\n\n    v_employee_id employees.employee_id%TYPE;\n    v_salary employees.salary%TYPE;\nBEGIN\n    OPEN emp_cursor;\n\n    LOOP\n        FETCH emp_cursor INTO v_employee_id, v_salary;\n        EXIT WHEN emp_cursor%NOTFOUND;\n        \n        -- Imagine an update is made to the employees table here by another transaction\n\n        -- The fetched values will still be from when the cursor was opened\n        DBMS_OUTPUT.PUT_LINE('Employee ID: ' || v_employee_id || ', Salary: ' || v_salary);\n    END LOOP;\n\n    CLOSE emp_cursor;\nEND;