DECLARE\n  b1   BOOLEAN;\n  b2   BOOLEAN;\n\n  FUNCTION checkit RETURN BOOLEAN IS\n  BEGIN\n    dbms_output.put_line('inside checkit');\n    RETURN TRUE;\n  END checkit;\n\n  PROCEDURE outp(n VARCHAR2, p BOOLEAN) IS\n  BEGIN\n    IF p THEN\n      dbms_output.put_line(n||' is true');\n    ELSE\n      dbms_output.put_line(n||' is false');\n    END IF;\n  END;\n\nBEGIN\n  b1 := checkit OR TRUE;\n  outp('b1',b1);\n  b2 := TRUE OR checkit;\n  outp('b2',b2);\nEND;