DECLARE\n    v_data VARCHAR2(4000);\n    CURSOR C1 IS SELECT data FROM table1 WHERE criteria = some_value;\n    CURSOR C2 IS SELECT data FROM table2 WHERE criteria = some_value;\nBEGIN\n    OPEN C1; FETCH C1 INTO v_data; CLOSE C1;\n    IF C1%FOUND THEN RETURN v_data; END IF;\n\n    OPEN C2; FETCH C2 INTO v_data; CLOSE C2;\n    IF C2%FOUND THEN RETURN v_data; END IF;\n\n    -- If no data found in either tables\n    RAISE_APPLICATION_ERROR(-20002, 'No data found');\nEXCEPTION\n    WHEN OTHERS THEN\n        CLOSE C1; CLOSE C2;\n        RAISE;\nEND;