DECLARE\n   needed_field_value VARCHAR2(100);\nBEGIN\n   SELECT needed_field INTO needed_field_value\n     FROM your_table\n    WHERE condition;\n   \n   -- Process needed_field_value if record is found\n   \nEXCEPTION\n   WHEN NO_DATA_FOUND THEN\n      -- Handle case when no data is found\n      NULL;  -- or some processing logic\n\n   WHEN OTHERS THEN\n      -- Handle other exceptions (optional but recommended)\n      RAISE;\nEND;