DECLARE\n    v_rows_affected NUMBER;\nBEGIN\n    -- Your UPDATE statement here\n    UPDATE your_table\n    SET column1 = 'new_value'\n    WHERE condition_column = 'condition_value';\n\n    -- Get the number of rows affected by the last DML statement\n    v_rows_affected := SQL%ROWCOUNT;\n\n    -- Optionally, you can use this value for further processing or logging\n    DBMS_OUTPUT.PUT_LINE('Rows updated: ' || v_rows_affected);\n    \nEND;