DECLARE\n    c_kilometers NUMBER;\nBEGIN\n    OPEN cursor_t;\n    LOOP\n        FETCH cursor_t INTO your_cursor_variables; -- Define this based on your cursor's output\n        EXIT WHEN cursor_t%NOTFOUND;\n\n        SELECT \n            (SELECT t.kilometers FROM table_name t WHERE t.code = cursor_t.code_att) +\n            (SELECT g.kilometers FROM table_name g WHERE g.code = cursor_t.code_aff)\n        INTO c_kilometers\n        FROM dual; -- Use dual for single value selection\n\n        -- Do something with c_kilometers here\n\n    END LOOP;\n    CLOSE cursor_t;\nEND;