proc sql;\n    connect to sqlservr (server=my-db-srvr database=SasProcSqlTest);\n\n    /* Create a local Items table from the source in SQL Server */\n    create table items as select * from connection to sqlservr (\n        SELECT * FROM SasProcSqlTest.dbo.tblItem\n    );\n\n    /* Update the local SAS dataset as needed */\n    update items\n    set Name = CATX('', Name, Name),\n        Value = Value * 2;\n\n    /* Insert data back into SQL Server table */\n    execute (insert into tblItem (Name, Value)\n             select Name, Value from connection to sqlservr (select * from work.items))\n    by sqlservr;\n\n    disconnect from sqlservr;\nquit;