-- Example of a function that returns the square of a number\nCREATE OR REPLACE FUNCTION get_square(num IN NUMBER) RETURN NUMBER IS\nBEGIN\n    RETURN num * num;\nEND;\n\n-- Example of a procedure that updates a table\nCREATE OR REPLACE PROCEDURE update_salary(emp_id IN NUMBER, new_sal OUT NUMBER) AS\nBEGIN\n    UPDATE employees SET salary = salary + 1000 WHERE employee_id = emp_id;\n    SELECT salary INTO new_sal FROM employees WHERE employee_id = emp_id;\nEND;