CREATE OR REPLACE FUNCTION remove_accents (p_string IN VARCHAR2)\nRETURN VARCHAR2 IS\n  l_result VARCHAR2(4000);\nBEGIN\n  FOR i IN 1 .. LENGTH(p_string) LOOP\n    l_result := l_result || TRIM(TRANSLATE(SUBSTR(p_string, i, 1),\n                                           'áéíóúÁÉÍÓÚ',\n                                           'aeiouAEIOU'));\n  END LOOP;\n  RETURN l_result;\nEND remove_accents;\n/\n\n-- Usage\nSELECT utl_match.edit_distance(remove_accents('á'), 'b') FROM dual;