DECLARE\n    v_sql VARCHAR2(1000);\nBEGIN\n    v_sql := 'SELECT * FROM some_non_existent_table';\n    EXECUTE IMMEDIATE v_sql;\nEXCEPTION\n    WHEN NO_DATA_FOUND THEN\n        DBMS_OUTPUT.PUT_LINE('No data found in ' || v_sql);\n    WHEN OTHERS THEN\n        IF SQLCODE = -942 THEN\n            DBMS_OUTPUT.PUT_LINE('ORA-00942: Table or view does not exist in query: ' || v_sql);\n        ELSE\n            RAISE;\n        END IF;\nEND;